INSERT INTO `award` (`id`, `year`, `description`, `publish`, `created_at`, `created_by`, `updated_at`, `updated_by`) VALUES
(1, 1997, '<ul>\r\n<li>RS St. Carolus terakreditasi penuh 5 bidang pelayanan</li>\r\n<li>Mendapat predikat RS Sayang Bayi</li>\r\n</ul>', 10, '2017-06-26 17:41:45', 1, '2017-12-03 21:44:22', 1),
(2, 2001, '<ul>\r\n<li>RS St. Carolus terakreditasi 12 bidang</li>\r\n</ul>\r\n', 10, '2017-06-26 17:42:33', 1, '2017-06-26 17:42:33', 1),
(3, 2007, '<ul>\r\n	<li>RS St. Carolus mendapat predikat RS Sayang Ibu dan Bayi</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"/upload/files/RSSIB%20Tingkat%20provinsi%202007..jpg\" style=\"height:364px; width:500px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Mendapat penghargaan LingkunganHidup yang baik</li>\r\n	<li>RS St. Carolus terakreditasi 16 bidang</li>\r\n</ul>\r\n', 10, '0000-00-00 00:00:00', 1, '2018-12-04 14:10:00', 1),
(4, 2010, '<ul><li>\r\nMendapatkan penghargaan sebagai RS Sayang Ibu dan Bayi terbaik di DKI Jakarta\r\n</li></ul>', 10, '0000-00-00 00:00:00', 1, NULL, NULL),
(5, 2011, '<ul><li>\r\nRS St. Carolus terakreditasi 16 bidang\r\n</li></ul>', 10, '0000-00-00 00:00:00', 0, NULL, NULL),
(6, 2013, '<ul>\r\n	<li>Mendapat penghargaan sebagai RS Sayang Ibu dan Bayi terbaik di DKI Jakarta</li>\r\n	<li><img alt=\"\" src=\"/upload/files/RSSIB%20Tingkat%20provinsi%202013.jpg\" style=\"height:359px; width:500px\" /></li>\r\n</ul>\r\n', 10, '0000-00-00 00:00:00', 1, '2018-12-04 13:57:05', 1),
(7, 2015, '<ul>\r\n	<li><strong>Lulus Akreditasi Rumah Sakit Tingkat Paripurna</strong></li>\r\n</ul>\r\n\r\n<p><strong><img alt=\"\" src=\"/upload/files/A4.jpg\" style=\"height:354px; width:500px\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Juara I &ldquo;Hand Hygine Dance Competition 2015&rdquo; yang diselenggarakan oleh Himpunan Perawat Pencegahan dan Pengendalian Infeksi cabang DKI Jakarta</li>\r\n</ul>\r\n', 10, '0000-00-00 00:00:00', 0, '2018-12-04 14:11:13', 1),
(8, 2016, '<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Mendapat Peringkat 3 Penghargaan sebagai RS Sayang Ibu dan Bayi terbaik di DKI Jakarta</li>\r\n	<li><img alt=\"\" src=\"/upload/files/carolus001%284%29.jpg\" style=\"height:348px; width:500px\" /></li>\r\n</ul>\r\n', 10, '2018-05-12 19:07:46', 1, '2018-12-04 13:53:53', 1),
(9, 2018, '<ul>\r\n	<li><strong>Lulus Akreditasi Rumah Sakit Tingkat Paripurna</strong></li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"/upload/files/SERTIFIKAT%20kecil.jpg\" style=\"height:626px; width:500px\" /></p>\r\n\r\n<ul>\r\n	<li>Penghargaan diberikan kepada Bank Darah RS St Carolus pada 20 Desember 2018, sebagai BDRS Terbaik Peringkat Pertama Tahun 2018, yang diberikan oleh kepala Unit Transfusi Darah PMI Provinsi DKI Jakarta dr. Salimar Salim, MARS. Tim Bank Darah Rumah Sakit St Carolus bernaung di bawah Instalasi Laboratorium. PMI dan Rumah Sakit merupakan sistem pelayanan bermutu dalam upaya penyediaan darah yang aman dan berkualitas untuk masyarakat. Dengan adanya koordinasi dan komunikasi yang terjalin baik antara UTD PMI Provinsi DKI Jakarta dengan Bank Darah Rumah Sakit diharapkan tercapai pelayanan prima.</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"/upload/files/Piagam%20Penghargaan%20BDRS%20St%20%20Carolus%283%29.JPG\" style=\"height:349px; width:500px\" /></p>\r\n', 10, '2018-12-24 13:19:54', 1, '2019-02-26 15:48:58', 1);
