
$(document).ready(function(){$(".carousel").carousel({interval:4000});var a=false;$(".carousel").on("click",".nav a",function(){a=true;$(".nav li").removeClass("active");$(this).parent().addClass("active")}).on("slid.bs.carousel",function(d){if(!a){var b=$(".carousel .nav").children().length-1;var c=$(".carousel .nav li.active");c.removeClass("active").next().addClass("active");var f=parseInt(c.data("slide-to"));if(b==f){$(".carousel .nav li").first().addClass("active")}}a=false})});